// src/App.js
import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import ListTarefas from "./components/ListTarefas";
import TarefasContext from "./context/TarefasContext";

function App() {
  const [novaTarefa, setNovaTarefa] = useState("");
  const { dispatch } = useContext(TarefasContext);

  useEffect(() => {
    axios.get('http://localhost:5001/api/tarefas')
      .then(response => {
        dispatch({ type: 'SET_TAREFAS', tarefas: response.data });
      })
      .catch(error => {
        console.error('Erro ao buscar tarefas:', error);
      });
  }, [dispatch]);

  const adicionarTarefa = () => {
    if (novaTarefa.trim()) {
      dispatch({ type: "ADICIONAR_TAREFA", nome: novaTarefa });
      setNovaTarefa("");
    }
  };

  return (
    <div>
      <h1>Gerenciador de Tarefas</h1>
      <input
        type="text"
        value={novaTarefa}
        onChange={(e) => setNovaTarefa(e.target.value)}
        placeholder="Adicionar nova tarefa"
      />
      <button onClick={adicionarTarefa}>Adicionar</button>
      <div>
        <button
          onClick={() => dispatch({ type: "FILTRAR_TAREFAS", filtro: "Todas" })}
        >
          Todas
        </button>
        <button
          onClick={() =>
            dispatch({ type: "FILTRAR_TAREFAS", filtro: "Concluídas" })
          }
        >
          Concluídas
        </button>
        <button
          onClick={() =>
            dispatch({ type: "FILTRAR_TAREFAS", filtro: "Pendentes" })
          }
        >
          Pendentes
        </button>
      </div>
      <ListTarefas />
    </div>
  );
}

export default App;
